INSERT INTO `info` VALUES ('117', '250', '60', '251', '1', '奥体中心', '北京市', null, null, null, '5', null, '[ [116.381784, 40.015823], [116.38208, 40.012549], [116.38738, 40.012991], [116.392914, 40.013109], [116.392734, 40.016107], [116.387461, 40.016003] ]', '', null, null, '北京国奥村项目总占地27.55公顷，总建筑面积50余万平米，由42栋6层或9层南北向电梯板楼精装公寓组成，分为ABCD四大组团。社区容积率仅1.5，绿化率高至40%，以180——280平方米的舒适型三、四居为户型主力。国奥村全面贯彻“绿色/科技/人文”三大奥运理念，运用再生水源热泵系统、集中式太阳能热水系统、景观花房生态污水处理系统等数十项领先国际的建筑技术创造出了超越时代的中国当代宜居典范。', 'data/syn_data/北京小区建筑道路图片/国奥村/1.jpg;data/syn_data/北京小区建筑道路图片/国奥村/2.jpg;data/syn_data/北京小区建筑道路图片/国奥村/3.jpg', null, null, null, null, null, null, 'data/syn_data/flash/北京奥运村（室内）.swf', '2', '2019-04-29 18:52:47', '2019-05-08 21:15:03');
