INSERT INTO `info` VALUES ('122', '351', '350', '350', null, '复兴路1', '上海市', null, null, null, '3', '[ [116.259482, 39.913504], [116.27116, 39.913698], [116.280467, 39.913822], [116.289899, 39.913794], [116.301487, 39.913698], [116.316776, 39.91349] ]', null, null, null, null, '复兴路位于海淀区南部。东起木樨地大桥，西至玉泉路。东与复兴门外大街相交，西与石景山路相接。因居于复兴门外，又要别于复兴门外大街，而称。复兴路是百里长安街的组成段落，两侧驻有铁道部（中国铁路总公司）、交通部、中联部、国家地震局、建行总行、有色金属设计总院、水利水电科学研究院（南院）、中央电视台、三○一总院以及城乡贸易中心、翠微大厦等。', 'data/syn_data/北京小区建筑道路图片/复兴路/1.jpg', null, null, null, null, null, null, 'data/syn_data/flash/北京复兴路.swf', '13', '2019-04-29 00:00:00', '2019-05-02 09:26:33');
