INSERT INTO `info` VALUES ('149', '79', '78', '78', null, '坦洲山公园（坦洲镇）', '中山市', null, null, '113.469572,22.269262', '1', null, null, null, null, null, '该公园位于中山坦洲镇联兴路与人民大街交叉北面，就在马路旁，山脚有健身设施供大家活动活动筋骨。', 'data/syn_data/zhongshan/坦洲山公园4.jpg', 'data/syn_data/zhongshan/坦洲山公园1.mp4', '75', null, null, null, null, null, null, '2019-04-30 00:00:00', null);
INSERT INTO `info` VALUES ('150', '81', '81', '81', null, '扬名广场', '珠海市', null, null, '113.586307,22.281031', '1', null, null, null, null, '珠海市香洲区凤凰南路1066号', '扬名广场坐落于老香洲，人流量大，四面临街，交通便利，多路公交车直达。也是珠海规模较大的综合mall，吃、喝、玩、乐、亲子、购物应有尽有。扬名百货首层以服装、鞋帽、化妆品、精品饰品等经营女性用品的主题商场；2-4层由世界500强企业“吉之岛”大型超市入驻，5-7层为扬名美居生活馆大型家私商场。是周末约上三五好友，一起逛逛街、吃吃饭的好选择。', 'data/syn_data/zhuhai/扬名广场1.jpg;data/syn_data/zhuhai/扬名广场2.jpg', 'data/syn_data/zhuhai/扬名广场1.mp4', '60', null, null, null, null, null, null, '2019-04-05 00:00:00', null);
INSERT INTO `info` VALUES ('151', '82', '81', '81', null, '扬名广场二期', '珠海市', null, null, '113.586307,22.281031', '1', null, null, null, null, null, '位于老香洲，靠近情侣路，珠海大剧院，民亭公园，属于珠海旅游景点区域。', 'data/syn_data/zhuhai/扬名广场3.jpg', 'data/syn_data/zhuhai/扬名广场2.mp4', '32', 'data/syn_data/zhuhai/扬名广场1.mp3', '83', null, null, null, null, '2019-04-20 00:00:00', null);
INSERT INTO `info` VALUES ('152', '86', '86', '86', null, '炮台山公园', '珠海市', null, null, '113.552316,22.232072', '1', null, null, null, null, '\r\n珠海市香洲区夏湾路60号', '炮台山公园位于珠海市香洲区，园内林木茂密，空气清新，据说站在山顶可以遥望到澳门，是市民游客休闲健身的好去处。', 'data/syn_data/zhuhai/炮台山公园1.jpg;data/syn_data/zhuhai/炮台山公园2.jpg', null, null, null, null, null, null, null, null, '2019-04-17 00:00:00', null);
INSERT INTO `info` VALUES ('153', '87', '86', '86', null, '炮台山公园（珠海）', '珠海市', null, null, '113.552316,22.232072', '1', null, null, null, null, null, '炮台山公园在夏湾，公园景色不错，面积很大，很多树木都标注了名。一层还有溜冰场，和小孩子玩的娱乐设施设备。', 'data/syn_data/zhuhai/炮台山公园3.jpg', null, null, null, null, null, null, null, null, '2019-04-29 00:00:00', null);
